mapper.update:
UPDATE customer
SET cust_status = FALSE
WHERE cust_seq = #{cust_seq}

UPDATE customer
SET
	cust_name = #{cust_name},
	cust_email = #{cust_email},
	cust_phone = #{cust_phone}
WHERE cust_seq = #{cust_seq}

UPDATE customer
SET cust_pw = #{cust_pw}
WHERE cust_seq = #{cust_seq}

mapper.select:
SELECT COUNT(*)
FROM customer
WHERE cust_id = #{cust_id}
