mapper.update:
update subscribe
set sub_cancel = true
where sub_seq = #{sub_seq}

update subscribe
set sub_total = #{sub_total}, sub_addrT = #{sub_addrT}, sub_addrD = #{sub_addrD},
	sub_name = #{sub_name}, sub_phone = #{sub_phone}, sub_card = #{sub_card},
	sub_upgrade = true, product_seq = #{product_seq}
where sub_seq = #{sub_seq}
